import React, { useState, useEffect } from "react";
import axios from "axios";

const EquipmentList = () => {
  const [equipment, setEquipment] = useState([]);

  useEffect(() => {
    fetchEquipment();
  }, []);

  const fetchEquipment = async () => {
    const response = await axios.get("/equipments/getAllEquipments");
    setEquipment(response.data);
  };

  const updateEquipment = async (id, updatedDetails) => {
    await axios.put(`/equipments/updateEquipment/${id}`, updatedDetails);
    fetchEquipment();
  };

  const deleteEquipment = async (id) => {
    await axios.delete(`/equipments/deleteEquipment/${id}`);
    fetchEquipment();
  };

  return (
    <div
      style={{
        margin: "2rem auto",
        padding: "2rem",
        maxWidth: "600px",
        background: "rgba(255, 255, 255, 0.9)",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", color: "#444", marginBottom: "1.5rem" }}>
        Equipment List
      </h2>
      <ul style={{ listStyle: "none", padding: "0" }}>
        {equipment.map((item) => (
          <li
            key={item.equipmentId}
            style={{
              padding: "1rem",
              border: "1px solid #ddd",
              borderRadius: "4px",
              marginBottom: "1rem",
              background: "#fff",
            }}
          >
            <p>
              <strong>Name:</strong> {item.name}
            </p>
            <p>
              <strong>Quantity:</strong> {item.quantity}
            </p>
            <p>
              <strong>Type:</strong> {item.type}
            </p>
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button
                onClick={() =>
                  updateEquipment(item.equipmentId, {
                    ...item,
                    quantity: item.quantity + 1,
                  })
                }
                style={{
                  backgroundColor: "#4caf50",
                  color: "white",
                  border: "none",
                  padding: "0.5rem 1rem",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Increase Quantity
              </button>
              <button
                onClick={() => deleteEquipment(item.equipmentId)}
                style={{
                  backgroundColor: "#e74c3c",
                  color: "white",
                  border: "none",
                  padding: "0.5rem 1rem",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Delete Equipment
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EquipmentList;
