import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentList = () => {
    const [students, setStudents] = useState([]);

    useEffect(() => {
        fetchStudents();
    }, []);

    const fetchStudents = async () => {
        const response = await axios.get('/students/getAllStudents');
        setStudents(response.data);
    };

    const updateStudent = async (id, updatedDetails) => {
        await axios.put(`/students/updateStudent/${id}`, updatedDetails);
        fetchStudents(); // Refresh students list
    };

    const deleteStudent = async (id) => {
        await axios.delete(`/students/deleteStudent/${id}`);
        fetchStudents(); // Refresh students list
    };

    return (
        <div>
            <h2>Student List</h2>
            <ul>
                {students.map(student => (
                    <li key={student.studentId}>
                        <p><strong>Name:</strong> {student.name}</p>
                        <p><strong>Program:</strong> {student.program}</p>
                        <p><strong>Year:</strong> {student.year}</p>
                        <button onClick={() => updateStudent(student.studentId, { ...student, year: student.year + 1 })}>
                            Promote Student
                        </button>
                        <button onClick={() => deleteStudent(student.studentId)}>
                            Delete Student
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default StudentList;
