import React, { useState } from 'react';
import axios from 'axios';

function CreateEquipment() {
  const [equipmentData, setEquipmentData] = useState({
    name: '',
    quantity: '',
    type: '',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post('/equipments/createEquipment', equipmentData)
      .then(() => {
        setMessage('Equipment created successfully!');
        setEquipmentData({ name: '', quantity: '', type: '' });
      })
      .catch(() => {
        setMessage('Error creating equipment');
      });
  };

  return (
    <div
      style={{
        backgroundImage: "url('/Equipmentbg.jpg')", // Update this to your background image
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <div
        style={{
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          padding: '20px',
          borderRadius: '8px',
          width: '00px',
          color: 'white',
        }}
      >
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Create Equipment</h2>
        <form onSubmit={handleSubmit}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Equipment Name:</label>
          <input
            type="text"
            placeholder="Enter Equipment Name"
            value={equipmentData.name}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, name: e.target.value })
            }
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />

          <label style={{ display: 'block', marginBottom: '5px' }}>
            Equipment Quantity:
          </label>
          <input
            type="number"
            placeholder="Enter Quantity"
            value={equipmentData.quantity}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, quantity: e.target.value })
            }
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />

          <label style={{ display: 'block', marginBottom: '5px' }}>
            Equipment Type:
          </label>
          <input
            type="text"
            placeholder="Enter Equipment Type"
            value={equipmentData.type}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, type: e.target.value })
            }
            required
            style={{
              width: '100%',
              padding: '10px',
              marginBottom: '15px',
              borderRadius: '5px',
              border: '1px solid #ccc',
            }}
          />

          <button
            type="submit"
            style={{
              width: '100%',
              padding: '10px',
              backgroundColor: '#fdb813',
              color: 'white',
              border: 'none',
              borderRadius: '5px',
              cursor: 'pointer',
              fontSize: '16px',
            }}
          >
            Create Equipment
          </button>
        </form>
        {message && (
          <p
            style={{
              textAlign: 'center',
              marginTop: '15px',
              color: message.includes('Error') ? 'red' : 'lightgreen',
            }}
          >
            {message}
          </p>
        )}
      </div>
    </div>
  );
}

export default CreateEquipment;
