import React, { useState } from 'react';
import axios from 'axios';
import './PaymentMethodStyles.css';
 
function CreatePaymentMethod() {
  const [paymentData, setPaymentData] = useState({
    paymentAmount: 0,
    paymentDate: '',
  });
  const [message, setMessage] = useState('');
 
  const handleSubmit = (event) => {
    event.preventDefault();
 
    // Validate payment amount (it should be positive)
    if (paymentData.paymentAmount <= 0) {
      setMessage('Payment amount must be greater than zero.');
      return;
    }
 
    // Validate payment date
    if (!paymentData.paymentDate) {
      setMessage('Please select a valid payment date.');
      return;
    }
 
    axios.post('/paymentMethods/createPaymentMethod', paymentData)
      .then(response => {
        setMessage('Payment method created successfully!');
        setPaymentData({
          paymentAmount: 0,
          paymentDate: '',
        });
      })
      .catch(error => {
        setMessage('Error creating payment method');
        console.error(error);
      });
  };
 
  return (
<div className="form-container">
<h2 className="form-title">Create Payment Method</h2>
<form onSubmit={handleSubmit} className="payment-form">
<label className="form-label">Payment Amount:</label>
<input
          type="number"
          placeholder="Enter Payment Amount"
          value={paymentData.paymentAmount}
          onChange={(e) => setPaymentData({ ...paymentData, paymentAmount: parseFloat(e.target.value) || 0 })}
          min="0"
          required
          className="input-field"
        />
<label className="form-label">Payment Date:</label>
<input
          type="date"
          value={paymentData.paymentDate}
          onChange={(e) => setPaymentData({ ...paymentData, paymentDate: e.target.value })}
          required
          className="input-field"
        />
<button type="submit" className="submit-btn">Create Payment Method</button>
</form>
      {message && <p className="form-message">{message}</p>}
</div>
  );
}
 
export default CreatePaymentMethod;
