import React, { useState } from 'react';
import axios from 'axios';
// Optional styling

function CreateStudent() {
  const [studentData, setStudentData] = useState({
    studentId: '',
    name: '',
    contactNumber: '',
    email: '',
    course: '',
    yearLevel: '',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate the email domain
    if (!studentData.email.endsWith('@cit.edu')) {
      setMessage('Invalid email domain. Use @cit.edu email.');
      return;
    }

    axios.post('/students/createStudent', studentData)
      .then(response => {
        setMessage('Student created successfully!');
        setStudentData({
          studentId: '',
          name: '',
          contactNumber: '',
          email: '',
          course: '',
          yearLevel: '',
        });
      })
      .catch(error => {
        setMessage('Error creating student');
        console.error(error);
      });
  };

  return (
    <div className="create-student-container">
      <h2>Create Student</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          placeholder="Enter Name"
          value={studentData.name}
          onChange={(e) => setStudentData({ ...studentData, name: e.target.value })}
          required
        />
        <label>Contact Number:</label>
        <input
          type="tel"
          placeholder="Enter Contact Number"
          value={studentData.contactNumber}
          onChange={(e) => setStudentData({ ...studentData, contactNumber: e.target.value })}
          required
        />
        <label>Email (@cit.edu only):</label>
        <input
          type="email"
          placeholder="Enter Email"
          value={studentData.email}
          onChange={(e) => setStudentData({ ...studentData, email: e.target.value })}
          required
        />
        <label>Course:</label>
        <input
          type="text"
          placeholder="Enter Course"
          value={studentData.course}
          onChange={(e) => setStudentData({ ...studentData, course: e.target.value })}
          required
        />
        <label>Year Level:</label>
        <input
          type="number"
          placeholder="Enter Year Level"
          value={studentData.yearLevel}
          onChange={(e) => setStudentData({ ...studentData, yearLevel: e.target.value })}
          required
        />
        <button type="submit" className="submit-button">Create Student</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default CreateStudent;
