import React, { useState } from 'react';
import axios from 'axios';
import './Student.css';
 
function CreateStudent() {
  const [studentData, setStudentData] = useState({
    name: '',
    program: '',
    year: '',
  });
  const [message, setMessage] = useState('');
 
  const handleSubmit = (event) => {
    event.preventDefault();
 
    axios
      .post('/students/createStudent', studentData)
      .then(() => {
        setMessage('Student created successfully!');
        setStudentData({ name: '', program: '', year: '' });
      })
      .catch(() => {
        setMessage('Error creating student');
      });
  };
 
  return (
<div className="container">
      <h2>Create Student</h2>
<form className="form-container" onSubmit={handleSubmit}>
<label>Name:</label>
<input
          type="text"
          placeholder="Enter Name"
          value={studentData.name}
          onChange={(e) => setStudentData({ ...studentData, name: e.target.value })}
          required
        />
 
        <label>Program:</label>
<input
          type="text"
          placeholder="Enter Program"
          value={studentData.program}
          onChange={(e) => setStudentData({ ...studentData, program: e.target.value })}
          required
        />
 
        <label>Year:</label>
<input
          type="number"
          placeholder="Enter Year"
          value={studentData.year}
          onChange={(e) => setStudentData({ ...studentData, year: e.target.value })}
          required
        />
 
        <button type="submit">Create Student</button>
</form>
      {message && <p className="message">{message}</p>}

      <style>{`
        .create-reservation-container {
          background-image: url('/Wildcats.jpg');
          background-size: cover;
          background-position: center;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 40px;
          min-height: 100vh;
          color: #fff;
        }
 
        h2 {
          background-color: rgba(0, 0, 0, 0.1);
          padding: 10px 20px;
          border-radius: 8px;
          margin-bottom: 30px;
        }
 
        form {
          background-color: rgba(0, 0, 0, 0.2);
          padding: 30px;
          border-radius: 10px;
          width: 100%;
          max-width: 500px;
          box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
          display: flex;
          flex-direction: column;
          align-items: center;
        }
 
        label {
          display: block;
          font-weight: bold;
          margin-bottom: 8px;
          color: #fff;
          width: 100%;
          text-align: left;
        }
 
        input {
          width: 100%;
          padding: 10px;
          margin-bottom: 20px;
          border: none;
          border-radius: 5px;
          outline: none;
          font-size: 1em;
        }
 
        button {
          background-color: #ffb703;
          color: #fff;
          border: none;
          padding: 12px 24px;
          cursor: pointer;
          border-radius: 5px;
          font-size: 1.1em;
          transition: background-color 0.3s;
          width: 100%;
        }
 
        button:hover {
          background-color: #e69902;
        }
 
        .message {
          margin-top: 20px;
          font-size: 1em;
          padding: 10px 20px;
          border-radius: 8px;
          background-color: rgba(0, 128, 0, 0.7);
          color: #fff;
        }
      `}</style>
</div>
  );
}
 
export default CreateStudent;
