// src/api/BookingService.js
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL;

export const getAllBookings = async () => {
    const response = await axios.get(`${API_BASE_URL}/bookings/getAllBookings`);
    return response.data;
};

export const getBookingById = async (id) => {
    const response = await axios.get(`${API_BASE_URL}/bookings/getBooking/${id}`);
    return response.data;
};

export const createBooking = async (booking) => {
    const response = await axios.post(`${API_BASE_URL}/bookings/createBooking`, booking);
    return response.data;
};

export const updateBooking = async (id, bookingDetails) => {
    const response = await axios.put(`${API_BASE_URL}/bookings/updateBooking/${id}`, bookingDetails);
    return response.data;
};

export const deleteBooking = async (id) => {
    await axios.delete(`${API_BASE_URL}/bookings/deleteBooking/${id}`);
};
