import React, { useState } from 'react';
import axios from 'axios';
 // Importing CSS file

function CreateEquipment() {
  const [equipmentData, setEquipmentData] = useState({
    name: '',
    description: '',
    quantity: 0,
    status: 'Available',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Send the data to the backend
    axios.post('/equipments/createEquipment', equipmentData)
      .then(response => {
        setMessage('Equipment created successfully!');
        setEquipmentData({
          name: '',
          description: '',
          quantity: 0,
          status: 'Available',
        });
      })
      .catch(error => {
        setMessage('Error creating equipment');
        console.error(error);
      });
  };

  return (
    <div className="create-equipment-container">
      <h2>Create Equipment</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          placeholder="Enter Equipment Name"
          value={equipmentData.name}
          onChange={(e) => setEquipmentData({ ...equipmentData, name: e.target.value })}
          required
        />

        <label>Description:</label>
        <input
          type="text"
          placeholder="Enter Equipment Description"
          value={equipmentData.description}
          onChange={(e) => setEquipmentData({ ...equipmentData, description: e.target.value })}
          required
        />

        <label>Quantity:</label>
        <input
          type="number"
          placeholder="Enter Quantity"
          value={equipmentData.quantity}
          onChange={(e) => setEquipmentData({ ...equipmentData, quantity: parseInt(e.target.value) })}
          min="1"
          required
        />

        <label>Status:</label>
        <select
          value={equipmentData.status}
          onChange={(e) => setEquipmentData({ ...equipmentData, status: e.target.value })}
          required
        >
          <option value="Available">Available</option>
          <option value="Not Available">Not Available</option>
        </select>

        <button type="submit" className="submit-button">Create Equipment</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default CreateEquipment;
