import React, { useState } from 'react';
import axios from 'axios';
 
function CreateReservation() {
  const [reservationData, setReservationData] = useState({
    reservationDate: '',
    reservationTime: '',
    status: 'Pending',
  });
  const [message, setMessage] = useState('');
 
  const handleSubmit = (event) => {
    event.preventDefault();
 
    axios.post('/reservations/createReservation', reservationData)
      .then(response => {
        setMessage('Reservation created successfully!');
        setReservationData({
          reservationDate: '',
          reservationTime: '',
          status: 'Pending',
        });
      })
      .catch(error => {
        setMessage('Error creating reservation');
        console.error(error);
      });
  };
 
  return (
<div
      className="create-reservation-container"
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL + '/Image/Wildcats.jpg'})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        padding: '40px',
        borderRadius: '10px',
        width: '40%',
        margin: 'auto',
        textAlign: 'center',
        color: '#fff',
      }}
>
<h2>Create Reservation</h2>
<form onSubmit={handleSubmit}>
<label>Reservation Date:</label>
<input
          type="date"
          value={reservationData.reservationDate}
          onChange={(e) => setReservationData({ ...reservationData, reservationDate: e.target.value })}
          required
        />
 
        <label>Reservation Time:</label>
<input
          type="time"
          value={reservationData.reservationTime}
          onChange={(e) => setReservationData({ ...reservationData, reservationTime: e.target.value })}
          required
        />
 
        <button type="submit">Create Reservation</button>
</form>
      {message && <p>{message}</p>}
 
      <style>{`
        h2 {
          color: #fff;
          margin-bottom: 20px;
        }
 
        label {
          display: block;
          color: #fff;
          margin-bottom: 5px;
        }
 
        input {
          width: 100%;
          padding: 8px;
          margin-bottom: 15px;
          border: 1px solid #ddd;
          border-radius: 5px;
        }
 
        button {
          background-color: #ffb703;
          color: #fff;
          border: none;
          padding: 10px 20px;
          cursor: pointer;
          border-radius: 5px;
          font-size: 1em;
        }
 
        button:hover {
          background-color: #e69902;
        }
      `}</style>
</div>
  );
}
 
export default CreateReservation;
