import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
 
// Styled Components for CSS
const Container = styled.div`
  font-family: Arial, sans-serif;
  margin: 20px;
`;
 
const Header = styled.h1`
  text-align: center;
  color: #333;
`;
 
const PaymentListContainer = styled.ul`
  list-style: none;
  padding: 0;
`;
 
const PaymentItem = styled.li`
  background-color: #f9f9f9;
  padding: 15px;
  margin: 10px 0;
  border: 1px solid #ddd;
  border-radius: 5px;
`;
 
const Button = styled.button`
  background-color: #3498db;
  color: white;
  border: none;
  padding: 8px 15px;
  margin: 5px;
  border-radius: 5px;
  cursor: pointer;
&:hover {
    background-color: #2980b9;
  }
`;
 
const CreatePaymentMethodContainer = styled.div`
  width: 300px;
  margin: 0 auto;
  padding: 20px;
  border-radius: 5px;
  background-color: #f9f9f9;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
`;
 
const Form = styled.form`
  display: flex;
  flex-direction: column;
`;
 
const Label = styled.label`
  margin-bottom: 5px;
  font-weight: bold;
`;
 
const Input = styled.input`
  margin-bottom: 15px;
  padding: 8px;
  font-size: 16px;
  border-radius: 5px;
  border: 1px solid #ddd;
`;
 
const SubmitButton = styled.button`
  background-color: #2ecc71;
  color: white;
  padding: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
&:hover {
    background-color: #27ae60;
  }
`;
 
const Message = styled.p`
  text-align: center;
  color: green;
`;
 
const PaymentMethodList = () => {
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [paymentData, setPaymentData] = useState({
    paymentAmount: 0,
    paymentDate: '',
  });
  const [message, setMessage] = useState('');
 
  useEffect(() => {
    fetchPaymentMethods();
  }, []);
 
  const fetchPaymentMethods = async () => {
    const response = await axios.get('/paymentMethods/getAllPaymentMethods');
    setPaymentMethods(response.data);
  };
 
  const updatePaymentMethod = async (id, updatedDetails) => {
    await axios.put(`/paymentMethods/updatePaymentMethod/${id}`, updatedDetails);
    fetchPaymentMethods(); // Refresh payment methods
  };
 
  const deletePaymentMethod = async (id) => {
    await axios.delete(`/paymentMethods/deletePaymentMethod/${id}`);
    fetchPaymentMethods(); // Refresh payment methods
  };
 
  const handleSubmit = (event) => {
    event.preventDefault();
 
    // Validate payment amount (it should be positive)
    if (paymentData.paymentAmount <= 0) {
      setMessage('Payment amount must be greater than zero.');
      return;
    }
 
    // Validate payment date
    if (!paymentData.paymentDate) {
      setMessage('Please select a valid payment date.');
      return;
    }
 
    axios.post('/paymentMethods/createPaymentMethod', paymentData)
      .then(response => {
        setMessage('Payment method created successfully!');
        setPaymentData({
          paymentAmount: 0,
          paymentDate: '',
        });
        fetchPaymentMethods(); // Refresh payment methods list
      })
      .catch(error => {
        setMessage('Error creating payment method');
        console.error(error);
      });
  };
 
  return (
<Container>
<Header>Payment Methods</Header>
<PaymentListContainer>
        {paymentMethods.map(paymentMethod => (
<PaymentItem key={paymentMethod.paymentMethodId}>
<p><strong>Payment Amount:</strong> â‚±{paymentMethod.paymentAmount.toFixed(2)}</p>
<p><strong>Payment Date:</strong> {new Date(paymentMethod.paymentDate).toLocaleDateString()}</p>
<Button onClick={() => updatePaymentMethod(paymentMethod.paymentMethodId, { ...paymentMethod, status: 'Inactive' })}>
              Deactivate
</Button>
<Button onClick={() => deletePaymentMethod(paymentMethod.paymentMethodId)}>
              Delete Payment Method
</Button>
</PaymentItem>
        ))}
</PaymentListContainer>
 
      <CreatePaymentMethodContainer>
<h2>Create Payment Method</h2>
<p><strong>Function:</strong> This function allows users to create a new payment method.</p>
<Form onSubmit={handleSubmit}>
<Label>Payment Amount:</Label>
<Input
            type="number"
            placeholder="Enter Payment Amount"
            value={paymentData.paymentAmount}
            onChange={(e) => setPaymentData({ ...paymentData, paymentAmount: parseFloat(e.target.value) })}
            min="0"
            required
          />
 
          <Label>Payment Date:</Label>
<Input
            type="date"
            value={paymentData.paymentDate}
            onChange={(e) => setPaymentData({ ...paymentData, paymentDate: e.target.value })}
            required
          />
 
          <SubmitButton type="submit">Create Payment Method</SubmitButton>
</Form>
        {message && <Message>{message}</Message>}
</CreatePaymentMethodContainer>
</Container>
  );
};
 
export default PaymentMethodList;
