import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EquipmentList = () => {
    const [equipments, setEquipments] = useState([]);

    useEffect(() => {
        fetchEquipments();
    }, []);

    const fetchEquipments = async () => {
        const response = await axios.get('/equipments/getAllEquipments');
        setEquipments(response.data);
    };

    const updateEquipment = async (id, updatedDetails) => {
        await axios.put(`/equipments/updateEquipment/${id}`, updatedDetails);
        fetchEquipments(); // Refresh equipment list
    };

    const deleteEquipment = async (id) => {
        await axios.delete(`/equipments/deleteEquipment/${id}`);
        fetchEquipments(); // Refresh equipment list
    };

    return (
        <div>
            <h2>Equipment List</h2>
            <ul>
                {equipments.map(equipment => (
                    <li key={equipment.id}>
                        <p><strong>Name:</strong> {equipment.name}</p>
                        <p><strong>Description:</strong> {equipment.description}</p>
                        <p><strong>Quantity:</strong> {equipment.quantity}</p>
                        <p><strong>Status:</strong> {equipment.status}</p>
                        <button onClick={() => updateEquipment(equipment.id, { ...equipment, status: 'Not Available' })}>
                            Mark as Not Available
                        </button>
                        <button onClick={() => deleteEquipment(equipment.id)}>
                            Delete Equipment
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EquipmentList;
