import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EquipmentList = () => {
  const [equipment, setEquipment] = useState([]);

  useEffect(() => {
    fetchEquipment();
  }, []);

  const fetchEquipment = async () => {
    const response = await axios.get('/equipments/getAllEquipments');
    setEquipment(response.data);
  };

  const updateEquipment = async (id, updatedDetails) => {
    await axios.put(`/equipments/updateEquipment/${id}`, updatedDetails);
    fetchEquipment();
  };

  const deleteEquipment = async (id) => {
    await axios.delete(`/equipments/deleteEquipment/${id}`);
    fetchEquipment();
  };

  return (
    <div
      style={{
        padding: "2rem",
        color: "#333",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <h2>Equipment List</h2>
      <ul
        style={{
          listStyleType: "none",
          padding: 0,
        }}
      >
        {equipment.map((item) => (
          <li
            key={item.equipmentId}
            style={{
              marginBottom: "1rem",
              padding: "1rem",
              border: "1px solid #ddd",
              borderRadius: "5px",
              backgroundColor: "#f9f9f9",
            }}
          >
            <p>
              <strong>Name:</strong> {item.name}
            </p>
            <p>
              <strong>Quantity:</strong> {item.quantity}
            </p>
            <p>
              <strong>Type:</strong> {item.type}
            </p>
            <button
              onClick={() =>
                updateEquipment(item.equipmentId, { ...item, quantity: item.quantity + 1 })
              }
              style={{
                marginRight: "0.5rem",
                padding: "0.5rem 1rem",
                backgroundColor: "green",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Increase Quantity
            </button>
            <button
              onClick={() => deleteEquipment(item.equipmentId)}
              style={{
                padding: "0.5rem 1rem",
                backgroundColor: "red",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Delete Equipment
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EquipmentList;
