import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EquipmentList = () => {
    const [equipmentList, setEquipmentList] = useState([]);

    useEffect(() => {
        fetchEquipment();
    }, []);

    const fetchEquipment = async () => {
        const response = await axios.get('/equipment/getAllEquipment');
        setEquipmentList(response.data);
    };

    const updateEquipment = async (id, updatedDetails) => {
        await axios.put(`/equipment/updateEquipment/${id}`, updatedDetails);
        fetchEquipment(); // Refresh equipment list
    };

    const deleteEquipment = async (id) => {
        await axios.delete(`/equipment/deleteEquipment/${id}`);
        fetchEquipment(); // Refresh equipment list
    };

    return (
        <div>
            <h2>Equipment List</h2>
            <ul>
                {equipmentList.map(equipment => (
                    <li key={equipment.equipmentId}>
                        <p><strong>Name:</strong> {equipment.equipmentName}</p>
                        <p><strong>Type:</strong> {equipment.equipmentType}</p>
                        <p><strong>Quantity:</strong> {equipment.quantity}</p>
                        {/* Removed Reservation Date and Status */}
                        <button onClick={() => updateEquipment(equipment.equipmentId, { ...equipment, quantity: equipment.quantity - 1 })}>
                            Decrease Quantity
                        </button>
                        <button onClick={() => updateEquipment(equipment.equipmentId, { ...equipment, quantity: equipment.quantity + 1 })}>
                            Increase Quantity
                        </button>
                        <button onClick={() => deleteEquipment(equipment.equipmentId)}>
                            Delete Equipment
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default EquipmentList;
