import React, { useState } from 'react';
import axios from 'axios';
 // Importing CSS file

function CreatePaymentMethod() {
  const [paymentMethodData, setPaymentMethodData] = useState({
    paymentMethodName: '',
    description: '',
    status: 'Active',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('/paymentMethods/createPaymentMethod', paymentMethodData)
      .then(response => {
        setMessage('Payment method created successfully!');
        setPaymentMethodData({
          paymentMethodName: '',
          description: '',
          status: 'Active',
        });
      })
      .catch(error => {
        setMessage('Error creating payment method');
        console.error(error);
      });
  };

  return (
    <div className="create-payment-method-container">
      <h2>Create Payment Method</h2>
      <p className="function-description">
        <strong>Function:</strong> This function allows users to create a new payment method.
      </p>
      <form onSubmit={handleSubmit}>
        <label>Payment Method Name:</label>
        <input
          type="text"
          placeholder="Enter Payment Method Name"
          value={paymentMethodData.paymentMethodName}
          onChange={(e) => setPaymentMethodData({ ...paymentMethodData, paymentMethodName: e.target.value })}
          required
        />

        <label>Description:</label>
        <textarea
          placeholder="Enter Description"
          value={paymentMethodData.description}
          onChange={(e) => setPaymentMethodData({ ...paymentMethodData, description: e.target.value })}
          required
        ></textarea>

        <label>Status:</label>
        <select
          value={paymentMethodData.status}
          onChange={(e) => setPaymentMethodData({ ...paymentMethodData, status: e.target.value })}
          required
        >
          <option value="Active">Active</option>
          <option value="Inactive">Inactive</option>
        </select>

        <button type="submit" className="submit-button">Create Payment Method</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default CreatePaymentMethod;
