// src/components/BookingForm.js
import React, { useState } from 'react';
import { createBooking, updateBooking } from '../api/BookingService';

function BookingForm({ bookingId, initialData = { bookingDate: '', duration: 1 }, onSuccess }) {
    const [booking, setBooking] = useState(initialData);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBooking({ ...booking, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (bookingId) {
            await updateBooking(bookingId, booking);
        } else {
            await createBooking(booking);
        }
        onSuccess();
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Date:
                <input type="date" name="bookingDate" value={booking.bookingDate} onChange={handleChange} required />
            </label>
            <label>
                Duration:
                <input type="number" name="duration" value={booking.duration} onChange={handleChange} required />
            </label>
            <button type="submit">Submit</button>
        </form>
    );
}

export default BookingForm;
