import React, { useState } from 'react';
import axios from 'axios';

function CreateBooking() {
  const [bookingData, setBookingData] = useState({
    bookingDate: '',
    duration: 1,
    name: '',
    contactNumber: '',
    email: '',
    paymentAmount: 0,
    startTime: '',
    endTime: '',
    status: 'Upcoming'
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    if (!bookingData.email.endsWith('@cit.edu')) {
      setMessage('Invalid email domain. Use @cit.edu email.');
      return;
    }

    axios.post('/bookings/createBooking', bookingData)
      .then(response => {
        setMessage('Booking created successfully!');
        setBookingData({
          bookingDate: '',
          duration: 1,
          name: '',
          contactNumber: '',
          email: '',
          paymentAmount: 0,
          startTime: '',
          endTime: '',
          status: 'Upcoming'
        });
      })
      .catch(error => {
        setMessage('Error creating booking');
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Create Booking</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Name or ID" required />
        <input type="tel" placeholder="Contact Number" required />
        <input type="email" placeholder="Email (@cit.edu only)" required />
        <input type="number" placeholder="Payment Amount" min="0" required />
        <input type="date" required />
        <input type="time" placeholder="Time to Come In" required />
        <input type="time" placeholder="Time to End Out" required />
        <button type="submit">Create Booking</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateBooking;
