import React, { useState } from 'react';
import axios from 'axios';
 // Importing CSS file

function CreateReservation() {
  const [reservationData, setReservationData] = useState({
    reservationDate: '',
    name: '',
    contactNumber: '',
    email: '',
    paymentAmount: 0,
    startTime: '',
    endTime: '',
    status: 'Pending',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate the email domain
    if (!reservationData.email.endsWith('@cit.edu')) {
      setMessage('Invalid email domain. Use @cit.edu email.');
      return;
    }

    axios.post('/reservations/createReservation', reservationData)
      .then(response => {
        setMessage('Reservation created successfully!');
        setReservationData({
          reservationDate: '',
          name: '',
          contactNumber: '',
          email: '',
          paymentAmount: 0,
          startTime: '',
          endTime: '',
          status: 'Pending',
        });
      })
      .catch(error => {
        setMessage('Error creating reservation');
        console.error(error);
      });
  };

  return (
    <div className="create-reservation-container">
      <h2>Create Reservation</h2>
      <form onSubmit={handleSubmit}>


        <label>Email (@cit.edu only):</label>
        <input
          type="email"
          placeholder="Enter Email"
          value={reservationData.email}
          onChange={(e) => setReservationData({ ...reservationData, email: e.target.value })}
          required
        />

        <label>Payment Amount:</label>
        <input
          type="number"
          placeholder="Enter Payment Amount"
          value={reservationData.paymentAmount}
          onChange={(e) => setReservationData({ ...reservationData, paymentAmount: parseFloat(e.target.value) })}
          min="0"
          required
        />

        <label>Reservation Date:</label>
        <input
          type="date"
          value={reservationData.reservationDate}
          onChange={(e) => setReservationData({ ...reservationData, reservationDate: e.target.value })}
          required
        />

        <label>Start Time:</label>
        <input
          type="time"
          value={reservationData.startTime}
          onChange={(e) => setReservationData({ ...reservationData, startTime: e.target.value })}
          required
        />

        <label>End Time:</label>
        <input
          type="time"
          value={reservationData.endTime}
          onChange={(e) => setReservationData({ ...reservationData, endTime: e.target.value })}
          required
        />

        <button type="submit">Create Reservation</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateReservation;
