import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Student.css'; // Import the CSS file
 
function StudentList() {
  const [students, setStudents] = useState([]);
 
  useEffect(() => {
    fetchStudents();
  }, []);
 
  const fetchStudents = async () => {
    const response = await axios.get('/students/getAllStudents');
    setStudents(response.data);
  };
 
  const updateStudent = async (id) => {
    await axios.put(`/students/updateStudent/${id}`, { year: 2024 }); // Example update
    fetchStudents();
  };
 
  const deleteStudent = async (id) => {
    await axios.delete(`/students/deleteStudent/${id}`);
    fetchStudents();
  };
 
  return (
<div className="student-list-container">
<h2>Student List</h2>
<ul>
        {students.map((student) => (
<li key={student.studentId}>
<p>
<strong>Name:</strong> {student.name}
</p>
<p>
<strong>Program:</strong> {student.program}
</p>
<p>
<strong>Year:</strong> {student.year}
</p>
<div className="action-buttons">
<button onClick={() => updateStudent(student.studentId)}>Promote</button>
<button onClick={() => deleteStudent(student.studentId)}>Delete</button>
</div>
</li>
        ))}
</ul>
</div>
  );
}
 
export default StudentList;
