import React, { useState } from 'react';
import axios from 'axios';

function CreateBooking() {
  const [bookingData, setBookingData] = useState({
    bookingDate: '',
    duration: 1,
    name: '',
    contactNumber: '',
    email: '',
    paymentAmount: 0,
    startTime: '',
    endTime: '',
    status: 'Upcoming'
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    // Validate the email domain
    if (!bookingData.email.endsWith('@cit.edu')) {
      setMessage('Invalid email domain. Use @cit.edu email.');
      return;
    }

    axios.post('/bookings/createBooking', bookingData)
      .then(response => {
        setMessage('Booking created successfully!');
        setBookingData({
          bookingDate: '',
          duration: 1,
          name: '',
          contactNumber: '',
          email: '',
          paymentAmount: 0,
          startTime: '',
          endTime: '',
          status: 'Upcoming'
        });
      })
      .catch(error => {
        setMessage('Error creating booking');
        console.error(error);
      });
  };

  return (
    <div>
      <h2>Create Booking</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name or ID"
          value={bookingData.name}
          onChange={(e) => setBookingData({ ...bookingData, name: e.target.value })}
          required
        />
        <input
          type="tel"
          placeholder="Contact Number"
          value={bookingData.contactNumber}
          onChange={(e) => setBookingData({ ...bookingData, contactNumber: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email (@cit.edu only)"
          value={bookingData.email}
          onChange={(e) => setBookingData({ ...bookingData, email: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Payment Amount"
          value={bookingData.paymentAmount}
          onChange={(e) => setBookingData({ ...bookingData, paymentAmount: parseFloat(e.target.value) })}
          min="0"
          required
        />
        <input
          type="date"
          value={bookingData.bookingDate}
          onChange={(e) => setBookingData({ ...bookingData, bookingDate: e.target.value })}
          required
        />
        <input
          type="time"
          placeholder="Time to Come In"
          value={bookingData.startTime}
          onChange={(e) => setBookingData({ ...bookingData, startTime: e.target.value })}
          required
        />
        <input
          type="time"
          placeholder="Time to End Out"
          value={bookingData.endTime}
          onChange={(e) => setBookingData({ ...bookingData, endTime: e.target.value })}
          required
        />
        <button type="submit">Create Booking</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateBooking;
