import React, { useState, useEffect } from 'react';
import axios from 'axios';
 
const ReservationList = () => {
    const [reservations, setReservations] = useState([]);
 
    useEffect(() => {
        fetchReservations();
    }, []);
 
    const fetchReservations = async () => {
        const response = await axios.get('/reservations/getAllReservations');
        setReservations(response.data);
    };
 
    const deleteReservation = async (id) => {
        await axios.delete(`/reservations/deleteReservation/${id}`);
        fetchReservations();
    };
 
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return date.toLocaleDateString(undefined, options);
    };
 
    const formatTime = (timeString) => {
        const time = new Date(`1970-01-01T${timeString}`);
        const hours = time.getHours();
        const minutes = time.getMinutes();
        const ampm = hours >= 12 ? 'PM' : 'AM';
        const formattedHours = hours % 12 || 12;
        const formattedMinutes = minutes < 10 ? `0${minutes}` : minutes;
        return `${formattedHours}:${formattedMinutes} ${ampm}`;
    };
 
    return (
<div className="reservation-list-container">
<h2>Current Bookings</h2>
<ul>
                {reservations.map(reservation => (
<li key={reservation.reservationId}>
<p><strong>Reservation Date:</strong> {formatDate(reservation.reservationDate)}</p>
<p><strong>Time In:</strong> {formatTime(reservation.startTime)}</p>
<button onClick={() => deleteReservation(reservation.reservationId)} className="delete-button">
                            Delete Reservation
</button>
</li>
                ))}
</ul>
<style>{`
                .reservation-list-container {
                  background-color: rgba(255, 255, 255, 0.9);
                  padding: 30px;
                  border-radius: 10px;
                  width: 40%;
                  margin: auto;
                  text-align: center;
                }
 
                h2 {
                  color: #333;
                  margin-bottom: 20px;
                }
 
                ul {
                  list-style: none;
                  padding: 0;
                }
 
                li {
                  margin-bottom: 15px;
                  border-bottom: 1px solid #ddd;
                  padding-bottom: 10px;
                }
 
                .delete-button {
                  background-color: #e63946;
                  color: #fff;
                  border: none;
                  padding: 5px 10px;
                  cursor: pointer;
                  border-radius: 5px;
                }
            `}</style>
</div>
    );
};
 
export default ReservationList;
