import React, { useState } from 'react';

function CreateEquipment() {
  const [equipmentData, setEquipmentData] = useState({
    name: '',
    quantity: '',
    type: '',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log('Equipment created:', equipmentData);
    setMessage('Equipment created successfully!');
    setEquipmentData({ name: '', quantity: '', type: '' });
  };

  const styles = {
    container: {
      backgroundImage: "url('/Equipmentbg.jpg')", // Ensure this image is in the `public` folder
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      minHeight: '100vh',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
    },
    formContainer: {
      backgroundColor: 'rgba(0, 0, 0, 0.7)', // Semi-transparent black
      padding: '20px',
      borderRadius: '8px',
      width: '300px',
      color: 'white',
      textAlign: 'center',
      boxShadow: '0 4px 10px rgba(0, 0, 0, 0.3)',
    },
    input: {
      width: '100%',
      padding: '10px',
      margin: '10px 0',
      borderRadius: '5px',
      border: '1px solid #ccc',
    },
    button: {
      width: '100%',
      padding: '10px',
      backgroundColor: '#ffa500', // Orange
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
    buttonHover: {
      backgroundColor: '#cc8400',
    },
    message: {
      marginTop: '10px',
      color: '#4caf50', // Success green
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.formContainer}>
        <h2>Create Equipment</h2>
        <form onSubmit={handleSubmit}>
          <label>Equipment Name:</label>
          <input
            type="text"
            placeholder="Enter Equipment Name"
            value={equipmentData.name}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, name: e.target.value })
            }
            style={styles.input}
            required
          />

          <label>Equipment Quantity:</label>
          <input
            type="number"
            placeholder="Enter Quantity"
            value={equipmentData.quantity}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, quantity: e.target.value })
            }
            style={styles.input}
            required
          />

          <label>Equipment Type:</label>
          <input
            type="text"
            placeholder="Enter Equipment Type"
            value={equipmentData.type}
            onChange={(e) =>
              setEquipmentData({ ...equipmentData, type: e.target.value })
            }
            style={styles.input}
            required
          />

          <button
            type="submit"
            style={styles.button}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#cc8400')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#ffa500')}
          >
            Create Equipment
          </button>
        </form>
        {message && <p style={styles.message}>{message}</p>}
      </div>
    </div>
  );
}

export default CreateEquipment;
