import React, { useState } from "react";
import axios from "axios";

function CreateEquipment() {
  const [equipmentData, setEquipmentData] = useState({
    name: "",
    quantity: 1,
    type: "",
  });
  const [message, setMessage] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post("/equipments/createEquipment", equipmentData)
      .then((response) => {
        setMessage("Equipment created successfully!");
        setEquipmentData({
          name: "",
          quantity: 1,
          type: "",
        });
      })
      .catch((error) => {
        setMessage("Error creating equipment");
        console.error(error);
      });
  };

  return (
    <div
      style={{
        margin: "2rem auto",
        padding: "2rem",
        maxWidth: "600px",
        background: "rgba(255, 255, 255, 0.9)",
        borderRadius: "8px",
        boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", color: "#444", marginBottom: "1.5rem" }}>
        Create Equipment
      </h2>
      <form onSubmit={handleSubmit}>
        <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
          Equipment Name:
        </label>
        <input
          type="text"
          placeholder="Enter Equipment Name"
          value={equipmentData.name}
          onChange={(e) =>
            setEquipmentData({ ...equipmentData, name: e.target.value })
          }
          required
          style={{
            width: "100%",
            padding: "0.5rem",
            marginBottom: "1rem",
            border: "1px solid #ccc",
            borderRadius: "4px",
          }}
        />
        <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
          Equipment Quantity:
        </label>
        <input
          type="number"
          placeholder="Enter Equipment Quantity"
          value={equipmentData.quantity}
          onChange={(e) =>
            setEquipmentData({
              ...equipmentData,
              quantity: parseInt(e.target.value),
            })
          }
          min="1"
          required
          style={{
            width: "100%",
            padding: "0.5rem",
            marginBottom: "1rem",
            border: "1px solid #ccc",
            borderRadius: "4px",
          }}
        />
        <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "bold" }}>
          Equipment Type:
        </label>
        <input
          type="text"
          placeholder="Enter Equipment Type"
          value={equipmentData.type}
          onChange={(e) =>
            setEquipmentData({ ...equipmentData, type: e.target.value })
          }
          required
          style={{
            width: "100%",
            padding: "0.5rem",
            marginBottom: "1rem",
            border: "1px solid #ccc",
            borderRadius: "4px",
          }}
        />
        <button
          type="submit"
          style={{
            backgroundColor: "#f4a900",
            color: "white",
            border: "none",
            padding: "0.75rem",
            width: "100%",
            borderRadius: "4px",
            fontSize: "1rem",
            cursor: "pointer",
          }}
        >
          Create Equipment
        </button>
      </form>
      {message && <p style={{ textAlign: "center", marginTop: "1rem" }}>{message}</p>}
    </div>
  );
}

export default CreateEquipment;
