import React, { useEffect, useState } from 'react';
import axios from 'axios';

function BookingList() {
  const [bookings, setBookings] = useState([]);
  const [editingBookingId, setEditingBookingId] = useState(null);
  const [updatedBookingDate, setUpdatedBookingDate] = useState('');
  const [updatedDuration, setUpdatedDuration] = useState(1);

  // Fetch bookings on component load
  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = () => {
    axios.get('/bookings/getAllBookings')
      .then(response => {
        setBookings(response.data);
      })
      .catch(error => {
        console.error('Error fetching bookings:', error);
      });
  };

  // Delete a booking by ID
  const handleDelete = (id) => {
    axios.delete(`/bookings/deleteBooking/${id}`)
      .then(() => {
        setBookings(bookings.filter(booking => booking.bookingId !== id));
      })
      .catch(error => {
        console.error('Error deleting booking:', error);
      });
  };

  // Set the booking that is currently being edited
  const handleEdit = (booking) => {
    setEditingBookingId(booking.bookingId);
    setUpdatedBookingDate(booking.bookingDate);
    setUpdatedDuration(booking.duration);
  };

  // Update a booking
  const handleUpdate = (id) => {
    const updatedBooking = {
      bookingDate: updatedBookingDate,
      duration: updatedDuration
    };

    axios.put(`/bookings/updateBooking/${id}`, updatedBooking)
      .then(() => {
        fetchBookings(); // Refresh the booking list after the update
        setEditingBookingId(null); // Reset editing state
      })
      .catch(error => {
        console.error('Error updating booking:', error);
      });
  };

  return (
    <div>
      <h2>Booking List</h2>
      <ul>
        {bookings.map(booking => (
          <li key={booking.bookingId}>
            {editingBookingId === booking.bookingId ? (
              // If the booking is being edited, show input fields
              <div>
                <input
                  type="date"
                  value={updatedBookingDate}
                  onChange={(e) => setUpdatedBookingDate(e.target.value)}
                />
                <input
                  type="number"
                  value={updatedDuration}
                  onChange={(e) => setUpdatedDuration(e.target.value)}
                  min="1"
                />
                <button onClick={() => handleUpdate(booking.bookingId)}>Save</button>
                <button onClick={() => setEditingBookingId(null)}>Cancel</button>
              </div>
            ) : (
              <div>
                {`Date: ${booking.bookingDate} | Duration: ${booking.duration} hour(s)`}
                <button onClick={() => handleEdit(booking)}>Edit</button>
                <button onClick={() => handleDelete(booking.bookingId)}>Delete</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookingList;
