import React, { useState, useEffect } from 'react';
import { createBooking, updateBooking } from '../services/BookingService';

const BookingForm = ({ selectedBooking, onFormSubmit }) => {
    const [booking, setBooking] = useState({
        bookingDate: '',
        duration: '',
    });

    useEffect(() => {
        if (selectedBooking) {
            setBooking(selectedBooking);
        }
    }, [selectedBooking]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBooking((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (booking.bookingId) {
            await updateBooking(booking.bookingId, booking);
        } else {
            await createBooking(booking);
        }
        onFormSubmit();
        setBooking({ bookingDate: '', duration: '' });
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>{booking.bookingId ? 'Edit Booking' : 'Create Booking'}</h2>
            <label>Date:</label>
            <input
                type="date"
                name="bookingDate"
                value={booking.bookingDate}
                onChange={handleChange}
                required
            />
            <label>Duration:</label>
            <input
                type="number"
                name="duration"
                value={booking.duration}
                onChange={handleChange}
                required
            />
            <button type="submit">{booking.bookingId ? 'Update' : 'Create'}</button>
        </form>
    );
};

export default BookingForm;
