import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ReservationList = () => {
    const [reservations, setReservations] = useState([]);

    useEffect(() => {
        fetchReservations();
    }, []);

    const fetchReservations = async () => {
        const response = await axios.get('/reservations/getAllReservations');
        setReservations(response.data);
    };

    const updateReservation = async (id, updatedDetails) => {
        await axios.put(`/reservations/updateReservation/${id}`, updatedDetails);
        fetchReservations(); // Refresh reservations
    };

    const deleteReservation = async (id) => {
        await axios.delete(`/reservations/deleteReservation/${id}`);
        fetchReservations(); // Refresh reservations
    };

    const formatDateTime = (dateString, timeString) => {
        const date = new Date(dateString);
        const time = new Date(`1970-01-01T${timeString}`);
        
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        const formattedDate = date.toLocaleDateString(undefined, options);
        
        const hours = time.getHours();
        const minutes = time.getMinutes();
        const ampm = hours >= 12 ? 'pm' : 'am';
        const formattedHours = hours % 12 || 12; // Convert to 12-hour format
        const formattedMinutes = minutes < 10 ? `0${minutes}` : minutes; // Add leading zero if needed
        
        return `${formattedDate} at ${formattedHours}:${formattedMinutes} ${ampm}`;
    };

    return (
        <div>
            <h1>Reservation List</h1>
            <ul>
                {reservations.map(reservation => (
                    <li key={reservation.reservationId}>
                        <p><strong>Start Time:</strong> {formatDateTime(reservation.reservationDate, reservation.startTime)}</p>
                        <p><strong>End Time:</strong> {formatDateTime(reservation.reservationDate, reservation.endTime)}</p>
                        <p><strong>Status:</strong> {reservation.status}</p>
                        <button onClick={() => updateReservation(reservation.reservationId, { ...reservation, status: 'Completed' })}>
                            Mark as Completed
                        </button>
                        <button onClick={() => deleteReservation(reservation.reservationId)}>
                            Delete Reservation
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ReservationList;
