import React, { useState } from 'react';
import axios from 'axios';


function CreateEquipment() {
  const [equipmentData, setEquipmentData] = useState({
    equipmentName: '',
    equipmentType: '',
    quantity: 1,
    reservationDate: '',
    status: 'Available',
  });
  const [message, setMessage] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();

    axios.post('/equipment/createEquipment', equipmentData)
      .then(response => {
        setMessage('Equipment reserved successfully!');
        setEquipmentData({
          equipmentName: '',
          equipmentType: '',
          quantity: 1,
          reservationDate: '',
          status: 'Available',
        });
      })
      .catch(error => {
        setMessage('Error creating equipment reservation');
        console.error(error);
      });
  };

  return (
    <div className="create-equipment-container">
      <h2>Create Equipment Reservation</h2>
      <p className="function-description">
        <strong>Function:</strong> Reserve new equipment.
      </p>
      <form onSubmit={handleSubmit}>
        <label>Equipment Name:</label>
        <input
          type="text"
          value={equipmentData.equipmentName}
          onChange={(e) => setEquipmentData({ ...equipmentData, equipmentName: e.target.value })}
          required
        />
        
        <label>Equipment Type:</label>
        <input
          type="text"
          value={equipmentData.equipmentType}
          onChange={(e) => setEquipmentData({ ...equipmentData, equipmentType: e.target.value })}
          required
        />
        
        <label>Quantity:</label>
        <input
          type="number"
          value={equipmentData.quantity}
          onChange={(e) => setEquipmentData({ ...equipmentData, quantity: parseInt(e.target.value) })}
          min="1"
          required
        />
        
        <label>Reservation Date:</label>
        <input
          type="date"
          value={equipmentData.reservationDate}
          onChange={(e) => setEquipmentData({ ...equipmentData, reservationDate: e.target.value })}
          required
        />
        
        <label>Status:</label>
        <select
          value={equipmentData.status}
          onChange={(e) => setEquipmentData({ ...equipmentData, status: e.target.value })}
          required
        >
          <option value="Available">Available</option>
          <option value="Reserved">Reserved</option>
        </select>

        <button type="submit">Reserve Equipment</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
}

export default CreateEquipment;
